// Use $input.all() to get all incoming items from the previous node
const allItems = $input.all();

// If no items, return early
if (!allItems || allItems.length === 0) {
  return [{ json: { prompt: "No data available to process." } }];
}

// Build a dynamic prompt
let prompt =  "Convert the following database result into a clear and natural language response to this question '"

prompt += $('Qstn').first().json.userMessage 

prompt += "' :\n\n";

// Loop through each item
allItems.forEach((item, index) => {
  // 'item.json' contains the actual row data from your SQL node
  const row = item.json;

  // row might have properties like row.ItemCode, row.ItemName, etc.
  // We'll collect keys dynamically
  const keys = Object.keys(row);

  prompt += `${index + 1}. `;
  keys.forEach((key) => {
    prompt += `**${key}**: ${row[key]}\n   `;
  });
  prompt += "\n";
}); 

prompt += "Make the response informative and user-friendly.";

// Return the prompt as JSON
return [{ json: { prompt } }];

